import React, { Component } from 'react';
import { Link }from 'react-router-dom';
import candyImage from "./img/Nerds-Gummy.png";
import './Candy.css';


class Candy extends Component {
   constructor(props) {
        super(props);
        this.state = { 
            candies: [],
        };
        this.handleClick = this.handleClick.bind(this);
     }

    handleClick() {
        const x = window.innerWidth * Math.random();
        const y = window.innerHeight * Math.random();
        this.setState(prevState => ({
            candies: [...prevState.candies, {x, y}]
        }));
    }

    render() {
        const bags = this.state.candies.map((bottle, i) => (
            <img 
                key={i} 
                src={candyImage} 
                className="candy" 
                style={{top: `${candy.y}px`, left: `${candy.x}px`}}
                alt="nerds candy"
            />
        ));
        return (
            <div className="Candy">
                <h1>sodas drank: {this.state.candies.length}</h1>
                <button onClick={this.handleClick}>NOM NOM NOM</button>
                <Link to="/">Go Back</Link>
                {bags}
            </div>
        );
    }
}

export default Soda;
